import React, { useState } from 'react';
import { Form, Button } from 'react-bootstrap';
import {useMediaQuery} from 'react-responsive';
import axios from 'axios';

import '../App.css';


{/* Contact page */}

function Contact() {

    const Desktop = ({ children }) => {
        const isDesktop = useMediaQuery({ minWidth: 992 })
        return isDesktop ? children : null
      }
      const Tablet = ({ children }) => {
        const isTablet = useMediaQuery({ minWidth: 768, maxWidth: 991 })
        return isTablet ? children : null
      }
      const Mobile = ({ children }) => {
        const isMobile = useMediaQuery({ maxWidth: 767 })
        return isMobile ? children : null
      }
      const Default = ({ children }) => {
        const isNotMobile = useMediaQuery({ minWidth: 768 })
        return isNotMobile ? children : null
      }

      const [firstname, setFirstName] = useState('')
      const [lastname, setLastName] = useState('')
      const [email, setEmail] = useState('')
      const [message, setMessage] = useState('')

      const handleClick = (e) => {
          e.preventDefault();

          if (e.target.id == "firstname") 
          {
            setFirstName(e.target.value)
          }

          if (e.target.id == "lastname")
          {
            setLastName(e.target.value)
          }

          if (e.target.id == "email")
          {
            setEmail(e.target.value)
          }

          if (e.target.id == "message")
          {
            setMessage(e.target.value)
          }
      }

      const handleSubmit = (e) => {
          e.preventDefault();

          const dataToSubmit = {
            firstname,
            lastname,
            email,
            message
          }
      }

      axios.post("api/sendMail", dataToSubmit);

    return (
        <div>
    <Desktop>
    <div className="controls">
        <div className="row">
            <div className="col-md-6">
                <div class="form-group">
                </div>
            </div>
            <div className="container">
                <div className="center">
                    <form action="POST">
                        <Form style={{ width: '400px'}}>
                            <Form.Group controlId="formBasicEmail">
                                <Form.Label>First Name</Form.Label>
                                <Form.Control type="firstname" placeholder="Jane" value="firstname" onChange={handleClick} />
                            </Form.Group>
                        </Form>
                        <Form style={{ width: '400px'}}>
                            <Form.Group controlId="formBasicEmail">
                                <Form.Label>Last Name</Form.Label>
                                <Form.Control type="lastname" placeholder="Doe" value="lastname" onChange={handleClick} />
                            </Form.Group>
                        </Form>
                        <Form style={{ width: '400px'}}>
                            <Form.Group controlId="formBasicEmail">
                                <Form.Label>Email</Form.Label>
                                <Form.Control type="Email" placeholder="janedoe@gmail.com" value="email" onChange={handleClick} />
                            </Form.Group>
                        </Form>
                        <Form style={{ width: '400px'}}>
                        <Form.Group controlId="exampleForm.ControlTextarea1">
                            <Form.Label>Subject</Form.Label>
                            <Form.Control as="textarea" rows="3" placeholder="Write something here..." value="message" onChange={handleClick} />
                        </Form.Group>
                        <Button className="d-inline-block" variant="primary" type="submit">
                                    Send
                        </Button>
                        </Form>
                    </form>
                </div>


            </div>
        </div>
    </div>
    </Desktop>

    <Mobile>
    <div className="controls">
        <div className="row">
            <div className="container">
                    <form action="POST">
                        <Form style={{ width: '400px'}}>
                            <Form.Group controlId="formBasicEmail">
                                <Form.Label>First Name</Form.Label>
                                <Form.Control type="firstname" placeholder="Jane" />
                            </Form.Group>
                        </Form>
                        <Form style={{ width: '400px'}}>
                            <Form.Group controlId="formBasicEmail">
                                <Form.Label>Last Name</Form.Label>
                                <Form.Control type="lastname" placeholder="Doe" />
                            </Form.Group>
                        </Form>
                        <Form style={{ width: '400px'}}>
                            <Form.Group controlId="formBasicEmail">
                                <Form.Label>Email</Form.Label>
                                <Form.Control type="Email" placeholder="janedoe@gmail.com" />
                            </Form.Group>
                        </Form>
                        <Form style={{ width: '400px'}}>
                        <Form.Group controlId="exampleForm.ControlTextarea1">
                            <Form.Label>Subject</Form.Label>
                            <Form.Control as="textarea" rows="3" placeholder="Write something here..." />
                        </Form.Group>
                        <Button className="d-inline-block" variant="primary" type="submit">
                                    Send
                        </Button>
                        </Form>
                    </form>
                </div>
            </div>
        </div>
    </Mobile>
    </div>
    );
}

export default Contact;
