import React from 'react';
import Image from 'react-image-resizer';

import code from '../assets/img/code.jpg'
import '../App.css';

{/* About page */}

function About() {
    const imageStyle = {
        float: 'right',
      };
    return (
        <div>
             <Image
          src={code}
          height={440}
          width={440}
          style={imageStyle}
        />
            <p className="App-style">Since 2019, we have been providing users and customers the best experience. <br></br>
            At ToUpper LLC, we pride and distinguish ourselves by using the most modern <br></br>
            technology and approach when creating applications for our clients. We help <br></br>
            our clients to ensure that their business succeeds and meets their goals by <br></br>
            providing the most elegant and easy to use application for their customers.

            </p>

            <p className="App-style"> During our process, we organize our requirements and make sure that your application <br></br>
            is met within a timely manner. In order to ensure that your application is the best out <br></br>
            there, we test our applications to assure for security, bugs, and any other issues that <br></br> 
            may come your way. We will also publish your application for you for the world to see   <br></br> 
            and provide any  maintenance that your application may need. For any S.E.O, surveys,    <br></br>
            or marketing needs, we will be happy to discuss on the best marketing approach for      <br></br> 
            your application!</p>

            <p className="App-style"> Let the geeks help! We know creating a website isn't easy, so let us do <br></br>
            the hard work for you. Please contact us and provide any questions  <br></br>
            or comments you may have! We will be happy to provide a free first <br></br> 
            consultation.
            </p>
      </div>
    );
}

export default About;
