import React, {useState, useEffect} from 'react';
import {useMediaQuery} from 'react-responsive'

import '../App.css';

{/* Privacy page */}

function Privacy() {
  const [isDesktop, setDesktop] = useState(window.innerWidth > 1450);

  const updateMedia = () => {
    setDesktop(window.innerWidth > 1024);
  };

  useEffect(() => {
    window.addEventListener("resize", updateMedia);
    return () => window.removeEventListener("resize", updateMedia);
  });
    return (
      <div>
        {isDesktop ? (
            <div className="center">
                <div className="word">
                    <p>We, at ToUpper LLC, understand the importance of privacy. When you use any of our applications, we try to 
                        make sure we give users the most up to date features. Some of these features may require to use some 
                        data. We use data to build better services and to make your life easier on a day to day basis. 
                        At ToUpper LLC, we do not solicit, steal, share or sell our data. We respect every 
                        individual's privacy and work hard to make sure that every user's private information is kept from public's reach.
                    </p>

                    <p>This privacy policy applies to all of our applications that are available for download on the Google Play Market or Apple App store.
                        This privacy policy also applies to our customer websites that may be using data as well in order to help their business on a day to day basis.
                        By using any of our web or mobile applications, you have read and agreed to our privacy policy statement and have also agreed to use our data services
                        that are being used on any of our applications. For any further details, please feel free to contact us.
                    </p>
                </div>
            </div>
           ): (
              <div> 
                <p>We, at ToUpper LLC, understand the importance of privacy. When you use any of our applications, we try to 
                  make sure we give users the most up to date features. Some of these features may require to use some 
                  data. We use data to build better services and to make your life easier on a day to day basis. 
                  At ToUpper LLC, we do not solicit, steal, share or sell our data. We respect every 
                  individual's privacy and work hard to make sure that every user's private information is kept from public's reach.
                </p>
              </div>
           )}
      </div>
    );
}

export default Privacy;
